import re
import os
import sys

__all__ = ['get_handlers']

def get_handlers():
    modname = globals()['__name__']
    dir_name = os.path.dirname(sys.modules[modname].__file__)
    sub_modules = set([os.path.splitext(x)[0] \
                     for x in os.listdir(dir_name) \
                     if re.match('[a-z].*\.py[oc]?', x)])
    result = {}
    for sub_module_name in sub_modules:
        sub_module = "%s.%s" % (modname, sub_module_name)
        __import__(sub_module)
        imported_module = sys.modules[sub_module]
        handler_class_name = getattr(imported_module, 'HANDLER_CLASS')
        result[sub_module_name] = getattr(imported_module, handler_class_name)
    return result
