from endian.emi import entities
from endian.core.lazylist import LazyList

__all__ = [
    "OpenSession"
]

def get_open_sessions():
    from endian.authentication.auth_client import list_open_sessions
    from endian.authentication_frontend.schema.scope import scope_title

    open_sessions = list_open_sessions() or {}
    sessions = []
    for scope, values in open_sessions.iteritems():
        title = scope_title(scope)
        for key, session in values.iteritems():
            sessions.append({
                'name': session['username'],
                'session_id': key,
                'scope': scope,
                'scope_title': title,
                'since': session['since'],
                'local': session.get('local', None),
                'remote': session.get('remote', None),
            })
    return sorted(sessions, cmp=lambda x,y: cmp(x['name'], y['name']))

class OpenSession(entities.Entity):
    entities.using_options(type='dynamic')

    exclude_from_search = [
        "since"
    ]

    ID = entities.Field(entities.Unicode, primary_key=True, hidden=True, autoincrement=True)
    session_id = entities.Field(entities.Unicode)
    name = entities.Field(entities.Unicode)
    scope = entities.Field(entities.Unicode)
    scope_title = entities.Field(entities.Unicode)
    since = entities.Field(entities.Integer)
    local = entities.Field(entities.Unicode) # local IP address
    remote = entities.Field(entities.Unicode) # remote IP address

    values = LazyList(get_open_sessions)

    @classmethod
    def all(cls):
        return list(cls.storage().load())
