from endian.core.i18n import _
from endian.validators.core import Multiline, String
from endian.emi import entities

__all__ = [
    'GuiProfile'
]

class GuiProfile(entities.Entity):
    entities.using_options(namespace='auth.guiprofile', type='yaml', register_datasource=False, storage_args={'indexes': ['name']})

    ID = entities.Field(entities.Integer,
        primary_key=True,
        autoincrement=True,
        hidden=True,
        expands=True)
    name = entities.Field(entities.Unicode,
        label_text=_("Profile name"),
        readonly_if_not_empty=True,
        nullable=False,
        help_text=_("Profile name must be unique"))
    white_list = entities.Field(entities.UnicodeText,
        label_text=_("Allowed pages"),
        validator=Multiline(String))
    black_list = entities.Field(entities.UnicodeText,
        label_text=_("Blocked pages"),
        validator=Multiline(String))
    remark = entities.Field(entities.Unicode,
        label_text=_("Remark"))

    @classmethod
    def get_by_id(cls, id_):
        try:
            id_ = int(id_)
        except (ValueError, TypeError):
            return None
        return cls.storage().load(id=id_)

    @classmethod
    def get_by_name(cls, name, check_acl=False):
        result = cls.storage().select(name=name, check_acl=check_acl)
        return result and result[0] or None

    @classmethod
    def all(cls):
        return cls.storage().load()

