from endian.emi import controllers

from endian.authentication_frontend.web.controllers.user import AllUserController
from endian.authentication_frontend.web.controllers.usergroup import AllUserGroupController
from endian.authentication_frontend.web.controllers.provider import AllProviderController
from endian.authentication_frontend.web.controllers.scope import AllScopeController
from endian.authentication_frontend.web.controllers.accounting import AllOpenSessionController
from endian.authentication_frontend.web.controllers.auth_test import ProviderAuthTestSchema, ProviderAuthTestController, \
                                                            ScopeAuthTestSchema, ScopeAuthTestController
from endian.authentication_frontend.web.controllers.guiuser import GuiUserController
from endian.authentication_frontend.web.controllers.guiprofile import GuiProfileController
from endian.authentication_frontend.web.widgets.auth_test import ProviderAuthTestSettings, ScopeAuthTestSettings

__all__ = [
    "AuthenticationController",
]

class SettingsController(controllers.MultiController):
    authserver = AllProviderController(name="authserver")
    service = AllScopeController(name="service")

    controllers = [authserver, service]
    # make sure they sync each other
    authserver.controllers = controllers
    service.controllers = controllers

class TestController(controllers.MultiController):
    show_divider = True

    providertest = ProviderAuthTestController(schema=ProviderAuthTestSchema,
        settingsWidget=ProviderAuthTestSettings,
        name="providertest")
    scopetest = ScopeAuthTestController(schema=ScopeAuthTestSchema,
        settingsWidget=ScopeAuthTestSettings,
        name="scopetest")

    controllers = [providertest, scopetest]

class AuthenticationController(controllers.Controller):
    user = AllUserController(name="user")
    usergroup = AllUserGroupController(name="usergroup")
    settings = SettingsController(name="settings")
    connection = AllOpenSessionController(name="connection")
    test = TestController(name="test")
    guiuser = GuiUserController(name="guiuser")
    guiprofile = GuiProfileController(name="guiprofile")

    @controllers.expose()
    def index(self):
        controllers.redirect("user")
