from endian.core.i18n import _
from endian.emi import widgets

from endian.authentication_frontend.schema.accounting import OpenSession

__all__ = [
    "DisconnectAction",
    "OpenSessionGrid"
]

class DisconnectAction(widgets.Action):
    ajax_support = True

    extraArgs = "&amp;name=%(name)s&amp;scope=%(scope)s&amp;session_id=%(session_id)s"
    name = "disconnect"
    image = "/toscawidgets/resources/endian.emi/static/images/action_connected.png"
    title = _("Disconnect")

    def condition(self, args):
        return args.get('scope') in ["openvpn", "ipsec-xauth"] and True or False

class OpenSessionGrid(widgets.KendoGrid):
    entity = OpenSession

    gridOnly = True

    sortable = True
    order_by = "name"
    order_sort = "asc"

    selectable = False

    columns = [
        widgets.Column(name="name", title=_("Username")),
        widgets.Column(name="scope_title", title=_("Service")),
        widgets.Column(name="since", title=_("Connected since"),
            kendo_column_template="#=kendo.toString(new Date(since * 1000), 'yyyy-MM-dd HH:mm:ss')#"),
        widgets.Column(name="local", title=_("Assigned IP"), empty_value=""),
        widgets.Column(name="remote", title=_("Remote IP"), empty_value=""),
        widgets.ActionsColumn([DisconnectAction])
    ]
