from endian.core.i18n import _
from endian.emi import widgets, fieldset
from endian.authentication_frontend.schema import GuiProfile

__all__ = [
    "GuiProfileEditor",
    "GuiProfileGrid"
]

class GuiProfileEditor(widgets.GridEditor):
    title = _("Add web frontend profile")
    edit_title = _("Change web frontend profile")
    columns = 2

    entity = GuiProfile

    children = fieldset.FieldsGroup(
        fields=GuiProfile.fields(fields=[
            "name",
            "remark",
            "black_list",
            "white_list",
            "ID"
        ])
    )


class GuiProfileGrid(widgets.KendoGrid):
    entity = GuiProfile

    new_label = _("Add web frontend profile")

    sortable = True
    selectable = False

    columns = [
        widgets.Column(name="name", title=_("Profile name"), empty_value=' '),
        widgets.Column(name="remark", title=_("Remark"), empty_value=' '),
        widgets.ActionsColumn(["edit", "delete"])
    ]

