from endian.core.i18n import _
from endian.emi import widgets, fieldset
from endian.emi.entities.fieldset import range_options

from endian.authentication_frontend.schema.scope import Scope, scope_title
from endian.authentication_frontend.web.widgets.validators import get_scope_validators


__all__ = [
    "get_scope_children",
    "get_scope_validators",
    "ScopeEditor",
    "ScopeGrid"
]

def get_scope_children(target_filter_value="*"):
    if target_filter_value == "*":
        providers = "providers"
    else:
        providers = widgets.MultipleSelectField("providers",
            title=_("Authentication backends"),
            label_text=_("Selected authentication backends"),
            sortable=True,
            options=range_options(info=dict(
                target="authentication_frontend:Provider",
                target_key="name", target_label="name",
                target_filter_key="provider_type",
                target_filter_value=target_filter_value
            )),
            expands=True)

    return fieldset.FieldsGroup(
        fields=Scope.fields(fields=[
            "name",
            providers,
            "ID"
        ])
    )

class ScopeEditor(widgets.GridEditor):
    title = _("Add new authentication server mapping")
    edit_title = _("Change authentication server mapping")

    entity = Scope

    children = get_scope_children()

    chained_validators = get_scope_validators()

class ScopeGrid(widgets.KendoGrid):
    entity = Scope

    new_label = _("Add new authentication server mapping")

    sortable = True
    order_by = "name"
    order_sort = "asc"

    selectable = False

    columns = [
        # checkbox = widgets.CheckBoxColumn(name='ID'),
        widgets.Column(name="name", title=_("Service"),
            format_function=scope_title),
        widgets.Column(name="providers", title=_("Authentication server"),
            format_function=lambda value: value and ", ".join(value) or "",
            empty_value="-"),
        widgets.ActionsColumn(["edit"])
    ]
