import os
import shutil
from endian.core import logger
from endian.migration.migration import mainHandler
from endian.job.commons import *

OLD_PATH = "/var/efw/pgsql/psql-latest.dump.bz2"
NEW_DIR = "/var/pgsql_backup"


def move_pgsql_backups():
    if not os.path.exists(NEW_DIR):
        os.makedirs(NEW_DIR)
    if os.path.exists(OLD_PATH):
        shutil.move(OLD_PATH, os.path.join(NEW_DIR, "psql-latest.dump.bz2"))


def main():
    try:
        move_pgsql_backups()
    except:
        logger.error("error migrating pgsql backup file", exc_info=True)

if __name__ == '__main__':
    mainHandler(main)
