from endian.ca import commands
from endian.ca.common import is_demo, DEMO_TEXT
from endian.ca.schema import Certificate
from endian.ca.web.widgets.ca import CertificateGrid, CreateCertificateSettings
from endian.ca.web.controllers.common import BaseCertificateGridController

__all__ = [
    "CertificateController",
]

class CertificateController(BaseCertificateGridController):
    entity = Certificate

    editorWidget = CreateCertificateSettings
    gridWidget = CertificateGrid

    do_store = False # we have a readonly storage
    do_delete = False # we have a readonly storage

    cert_option_field = "cert_option"

    def onDelete(self, cert_id, item):
        if is_demo():
            raise Exception(DEMO_TEXT)
        if cert_id.endswith('cert.pem'):
            cert_id= cert_id[:-8]
        data = commands.revoke_certificate(cert_id)
        if 'error' in data:
            raise Exception(data['error'])
        return True

