import os
from endian.emi import controllers
from endian.emi.framework import response

from endian.ca.common import is_demo
from endian.authentication.handlers.ca import CA_CRL
from endian.ca.schema import RevokedCertificate
from endian.ca.web.widgets.ca import RevokedCertificateGrid

__all__ = [
    "RevokedCertificateController",
]

class RevokedCertificateController(controllers.GridController):
    entity = RevokedCertificate
    gridWidget = RevokedCertificateGrid

    @controllers.expose(template="endian.ca.web.templates.revoked_certificates")
    def index(self, **args):
        result = self.index_internal(**args)
        result['has_crl'] = os.path.exists(CA_CRL)
        return result

    @controllers.expose(content_type="application/x-download")
    def crl(self, **args):
        """ Return the crl file """
        response.headers["Content-Disposition"] = 'attachment; filename="revocationlist.crl"'
        if is_demo():
            return ""
        f = None
        try:
            try:
                f = file(CA_CRL, 'r')
                return f.read()
            finally:
                if f is not None:
                    f.close()
        except:
            return ""

