from inspect import isclass

__author__ = "Andrea Boomi <a.bonomi@endian.com>"
__date__ = "2010-05-13"

"""
Defines generic class utils
"""

class classproperty(object):
    """
        Return a read-only class property attribute for new-style classes (classes that derive from object).
       
        :param fget: fget is a function for getting an attribute value
        :type fget: function

        It possible to create read-only class properties easily using property()  as a decorator:
        
        class Angle(object):
        
            @classproperty
            def max(cls):
                return 90

        print Angle.max
    """
    def __init__(self, fget):
        self.fget = fget

    def __get__(self, instance, owner):
        return self.fget(isclass(owner) and owner or type(owner))


__all__ = ['classproperty']
