def diff(a, b):
    """
    Compares the both dictionaries a and b and returns a dictionary
    with the differencies.

    The returning dict holds for each key the differencies as a tuple
    of the value of a and the value of b.

    Raises AttributeError if one of both is not a dict.
    """

    keys = []
    keys.extend(a.keys())
    keys.extend(b.keys())
    uniquekeys = set(keys)

    ret = {}
    if a == b:
        return ret

    for key in uniquekeys:
        vala = None
        valb = None
        try:
            vala = a[key]
        except:
            pass
        try:
            valb = b[key]
        except:
            pass
        if vala == valb:
            continue
        ret[key] = (vala,valb)
    return ret
