__author__ = "Andrea Bonomi <a.bonomi@endian.com>"
__date__ = "2011-11-15"

__all__ = [ 'enumerate_plus' ]

def enumerate_plus(sequence, start=0):
    """
        Enhanced  version of the enumerate function

        :param sequence: a sequence
        :type sequence: must be a sequence, an iterator,
                        or some other object which supports iteration.
        :return The next() method of the iterator returned by enumerate_plus() 
                        returns a tuple containing:
                * a count (from start which defaults to 0),
                * a boolean, True if the current item is the first of the sequence
                * a boolean, True if the current item is the last of the sequence
                * the current value in the sequence
    """
    count = start
    iter = sequence.__iter__()
    first = True
    last = False
    next = iter.next()
    while not last:
        item = next
        try:
            next = iter.next()
        except StopIteration:
            next = None
            last = True
        yield (count, first, last, item)
        count += 1
        first = False
    raise StopIteration
