class MagicSet(list):
    """
    A list that acts like a dictionary
    """

    _sort = None

    def __init__(self, items=None, identifier="id"):
        self.extend(items)
        self._identifier = identifier

    def _getID(self, item):
        if type(item) == dict:
            return item.get(self._identifier)
        elif hasattr(item, self._identifier):
            return getattr(item, self._identifier)
        return None

    def _getValue(self, key):
        for item in self:
            if key == self._getID(item):
                return item
        raise KeyError(key)

    def __getitem__(self, key):
        if type(key) == int:
            return list.__getitem__(self, key)
        return self._getValue(key)

    def get(self, key, default=None):
        try:
            return self.__getitem__(key)
        except KeyError:
            return default

    def keys(self):
        if self._sort:
            return self._sort
        keys = []
        for item in self:
            key = self._getID(item)
            if key != None:
                keys.append(key)
        return keys
