__author__ = "Andrea Bonomi <a.bonomi@endian.com>"
__date__ = "2016-06-16"

import os
import shutil
try:
    import cPickle as pickle
except:
    import pickle
from endian.core.abstractnvstorage import AbstractNVStorage

__all__ = [
    'nvstorage',
    'AbstractNVStorage',
    'FilesystemNVStorage'
]

class FilesystemNVStorage(AbstractNVStorage):

    __filename = '/var/efw/main/nvstorage'

    def _read(self, op=None, key=None):
        if not os.access(self.__filename, os.R_OK):
            return {}
        try:
            with open(self.__filename, 'rb') as f:
                return pickle.load(f)
        except Exception:
            return {}

    def _write(self, data, op, key):
        temp_filename = self.__filename + '.tmp'
        f = open(temp_filename, 'wb')
        try:
            try:
                pickle.dump(data, f, 2)
            except Exception:
                os.remove(temp_filename)
                raise
        finally:
            f.close()
        shutil.move(temp_filename, self.__filename)

try:
    # Import platform specific implementation
    from endian.platform import nvstorage
except ImportError:
    # Use the default (filesystem) implementation
    nvstorage = FilesystemNVStorage()

