from endian.core.i18n import _
from endian.emi.entities import Entity, using_options, Field, setup_entities, Unicode, String, Boolean

INTERVAL = [
    {'id': '2500', 'name': "%1.1f %s" %(2.5, _("seconds"))},
    {'id': '5000', 'name': "5 %s" % _("seconds")},
    {'id': '10000', 'name': "10 %s" % _("seconds")},
    {'id': '30000', 'name': "30 %s" % _("seconds")},
    {'id': '60000', 'name': "1 %s" % _("minute")}
]

DEFAULT_CONFIGURATION = [
    {
        "name":'SystemInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":60000,
        "column":0,
        "row":0
    },
    {
        "name":'SignaturesInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":60000,
        "column":0,
        "row":1
    },
    {
        "name":'HardwareInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":5000,
        "column":0,
        "row":2
    },
    {
        "name":'ServiceInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":10000,
        "column":0,
        "row":3
    },
    {
        "name":'NetworkInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":10000,
        "column":1,
        "row":0
    },
    {
        "name":'UpLinkInformationPlugin',
        "enabled":True,
        "closed":False,
        "interval":5000,
        "column":1,
        "row":1
    },
    {
        "name":'JobsInformationPlugin',
        "enabled":False,
        "closed":False,
        "interval":10000,
        "column":1,
        "row":2
    }
]

class IntervalOptions(Entity):
    using_options(type='const')

    id = Field(Unicode, primary_key=True, nullable=False)
    name = Field(Unicode, nullable=False)

    values = INTERVAL

class DashBoardConfig(Entity):
    using_options(namespace='dashboard.config', type='csv', register_datasource=True)

    name = Field(String, label_text=_("Name"))
    enabled = Field(Boolean, label_text=_("Enabled"))
    closed = Field(Boolean, label_text=_("Closed"))
    interval = Field(String, label_text=_("Update Interval"), target="dashboard:IntervalOptions")
    column = Field(String, label_text=_("Column"))
    row = Field(String, label_text=_("Row"))

entities = [
    IntervalOptions,
    DashBoardConfig
]

setup_entities(prefix="dashboard", entities=entities)

__all__ = [
    "DashBoardConfig",
    "DEFAULT_CONFIGURATION"
]
