from endian.emi.controllers import PluginController, GridController, MultiController
from endian.dashboard.schema import DashBoardConfig
from endian.dashboard.web.widgets import DashBoardPlugins, DashBoardSettings

class DashBoardController(MultiController):
    hidecontentbox = True
    show_function = "naked"

    settings = GridController(gridWidget=DashBoardSettings,
                              entity=DashBoardConfig,
                              hideable=True,
                              name="settings",
                              hidecontentbox=False)
    plugins = PluginController(pluginsWidget=DashBoardPlugins,
                               entity=DashBoardConfig,
                               name="plugins",
                               hidecontentbox=False)

    controllers = [
        settings,
        plugins
    ]

__all__ = [
    'DashBoardController'
]
