"""
.. mod:: hardwareinformationplugin - Contains the dashboard plugin
 HardwareInformationPlugin.

.. synopsis:: Contains the dashboard plugin HardwareInformationPlugin.

.. automodule:: hardwareinformationplugin

Contains the dashboard plugin HardwareInformationPlugin.

.. moduleauthor:: Alber Maximilian

"""
__all__ = [
    'HardwareInformationPlugin'
]

from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin

from endian.dashboard.util import\
    getCPUCount,\
    getStorageInformation

class HardwareInformationPluginContent(FormField):
    """
    Generates the the content of the HardwareInformationPlugin.
    """

    template = "endian.dashboard.web.templates.hardwareinformationplugincontent"
    engine_name = "mako"

    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/hardwareinformationcontent.css')
    ]

    cpuCount = 0
    percentages = {}

    params = ["cpuCount", "percentages"]

    def __init__(self, **params):
        """
        Initilaze the object.
        """
        FormField.__init__(self, **params)

        self.cpuCount = getCPUCount()
        self.percentages = getStorageInformation()

class HardwareInformationPlugin(CloseablePlugin):
    """
    A dashboard plugin which display the hardware states of
    the firewall.
    """

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/hardwareinformationplugin.js')
    ]

    pluginName = _("Hardware Information Plugin")
    pluginDescription = _("Shows the main hardware information of the firewall.")

    def __init__(self, **params):
        """
        Initilaze the object.
        """
        CloseablePlugin.__init__(
            self,
            jsInitFunction="hardwareinformationpluginUpdate",
            initURL="/manage/status/status.dashboard.hardware",
            jsUpdateFunction="hardwareinformationpluginUpdate",
            updateURL="/manage/status/status.dashboard.hardware",
            header=_("Hardware information"),
            content=HardwareInformationPluginContent(**params),
            **params
            )
