__all__ = [ 'JobsInformationPlugin' ]

from tw.forms import FormField
from tw.api import JSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin

class JobsInformationPluginContent(FormField):
    """
       Generates the the content of the JobsInformationPlugin.
    """

    template = "endian.dashboard.web.templates.jobsinformationplugincontent"
    engine_name = "mako"

class JobsInformationPlugin(CloseablePlugin):
    """
       A dashboard plugin which display informations about the jobs
    """

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/jobsinformationplugin.js')
    ]

    pluginName = _("Jobs Information Plugin")
    pluginDescription = _("Shows information about the jobs.")

    def __init__(self, **params):
        CloseablePlugin.__init__(
            self,
            jsInitFunction="jobsinformationpluginInit",
            initURL="/manage/status/status.jobsengine.jobs",
            jsUpdateFunction="jobsinformationpluginUpdate",
            updateURL="/manage/status/status.jobsengine.jobs",
            header=_("Jobs"),
            content=JobsInformationPluginContent(**params),
            **params
            )

