"""
.. mod:: networkinformationplugin - Contains the dashboard plugin
 NetworkInformationPlugin.

.. synopsis:: Contains the dashboard plugin NetworkInformationPlugin.

.. automodule:: networkinformationplugin

Contains the dashboard plugin NetworkInformationPlugin.

.. moduleauthor:: Alber Maximilian
"""
__all__ = [
    'NetworkInformationPlugin'
]

from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets.static import jquery_flot
from endian.emi.widgets import CloseablePlugin

DEFAULT_MAX_INTERFACES = 6

class NetworkInformationPluginContent(FormField):
    """
    Generates the the content of the NetworkInformationPlugin.
    """

    template = "endian.dashboard.web.templates.networkinformationplugincontent"
    engine_name = "mako"

    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/networkinformationcontent.css')
    ]

    headers = []
    interfaces = []
    interfacesNamesSorted = []
    maxInterfaces = DEFAULT_MAX_INTERFACES

    params = ["maxInterfaces"]

class NetworkInformationPlugin(CloseablePlugin):
    """
    A dashboard plugin which display informations about
    the network of the firewall system.
    """

    javascript = CloseablePlugin.javascript + [
        jquery_flot,
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/networkinformationplugin.js')
    ]

    pluginName = _("Network Information Plugin")
    pluginDescription = _("Shows information about the network of the firewall.")

    def __init__(self, **params):
        """
        Initilaze the object.
        """
        CloseablePlugin.__init__(
            self,
            jsInitFunction="networkinformationpluginInit",
            initURL="/manage/status/status.dashboard.network",
            jsUpdateFunction="networkinformationpluginUpdate",
            updateURL="/manage/status/status.collectd.singlevalues?timeout=4&plugin=network",
            header=_("Network Interfaces"),
            content=NetworkInformationPluginContent(**params),
            **params
            )
