"""
.. mod:: serviceinformationplugin - Contains the dashboard plugin
 ServiceInformationPlugin.

.. synopsis:: Contains the dashboard plugin ServiceInformationPlugin.

Contains the dashboard plugin ServiceInformationPlugin.
"""

import os
from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin

__all__ = [
    'ServiceInformationPlugin'
]

COLLECTD_KEYS = [
    'memory/memory-used',
    'filecount-postfix_queue/files',
    'tail-smtp/connections-noqueue',
    'tail-smtp/connections-virus',
    'tail-smtp/connections-spam',
    'tail-smtp/connections-clean',
    'tail-smtp/connections-incoming',
    'tail-smtp/connections-sent',
    'tail-pop/connections-spam',
    'tail-pop/connections-virus',
    'tail-pop/connections-scanned',
    'tail-http/connections-hit',
    'tail-http/connections-miss',
    'tail-http/connections-denied',
    'tail-http/connections-virus'
]

SERVICES = {
    '/usr/lib/efw/smtpscan/default/settings': {
        "ID": "postfix",
        "NAME": _('SMTP Proxy'),
        "LIVELOG": "smtp"
    },
    '/usr/lib/efw/proxy/default/settings': {
        "ID": "squid",
        "NAME": _('HTTP Proxy'),
        "LIVELOG": "dansguardian,squid"
    },
    '/usr/lib/efw/snort/default/settings': {
        "ID": "snort",
        "NAME": _('Intrusion Detection'),
        "LIVELOG": "snort"
    },
    '/usr/lib/efw/p3scan/default/settings': {
        "ID": "p3scan",
        "NAME": _('POP3 proxy'),
        "LIVELOG": False
    }
}


class ServiceInformationPluginContent(FormField):
    """Generates the the content of the ServiceInformationPlugin."""

    template = "endian.dashboard.web.templates.serviceinformationplugincontent"
    engine_name = "mako"

    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/serviceinformationcontent.css')
    ]

    serviceInformation = [val for (key, val) in SERVICES.iteritems() if os.path.exists(key)]

    params = ["serviceInformation"]


class ServiceInformationPlugin(CloseablePlugin):
    """A dashboard plugin which display informations about the services of the firewall system."""

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/serviceinformationplugin.js'),
    ]

    pluginName = _("Service Information Plugin")
    pluginDescription = _("Shows information about the services on the firewall.")

    def __init__(self, **params):
        """Initilaze the object."""
        CloseablePlugin.__init__(
            self,
            jsInitFunction="serviceinformationpluginInit",
            initURL="/manage/status/status.dashboard.service",
            jsUpdateFunction="serviceinformationpluginUpdate",
            updateURL="/manage/status/status.collectd.singlevalues?timeout=4&plugin=service",
            updateParams={"keys": COLLECTD_KEYS},
            header=_("Services (<a href=\"javascript:void(0);\" onclick=\"serviceinformationplugin_openLogs();\">Live Log</a>)"),
            content=ServiceInformationPluginContent(**params),
            **params
        )
