__all__ = [ 'SignaturesInformationPlugin' ]

from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin


class SignaturesInformationPluginContent(FormField):
    """
       Generates the the content of the SignaturesInformationPlugin.
    """

    template = "endian.dashboard.web.templates.signaturesinformationplugincontent"
    engine_name = "mako"
    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/signaturesinformationcontent.css')
    ]


class SignaturesInformationPlugin(CloseablePlugin):
    """
       A dashboard plugin which display informations about the signatures
    """

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/signaturesinformationplugin.js')
    ]

    pluginName = _("Signatures Information Plugin")
    pluginDescription = _("Shows information about the signatures.")

    def __init__(self, **params):
        CloseablePlugin.__init__(
            self,
            jsInitFunction="signaturesinformationpluginUpdate",
            initURL="/manage/status/status.dashboard.signatures",
            jsUpdateFunction="signaturesinformationpluginUpdate",
            updateURL="/manage/status/status.dashboard.signatures",
            header=_("Signature updates"),
            content=SignaturesInformationPluginContent(**params),
            **params)
