"""
.. mod:: systeminformationplugin - Contains the dashboard plugin
 SystemInformationPlugin.

.. synopsis:: Contains the dashboard plugin SystemInformationPlugin.

.. automodule:: systeminformationplugin

Contains the dashboard plugin SystemInformationPlugin.

.. moduleauthor:: Alber Maximilian
"""

__all__ = [
    'SystemInformationPlugin'
]

from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin
from endian.dashboard.util import getHostName

class SystemInformationPluginContent(FormField):
    """
    Generates the the content of the SystemInformationPlugin.
    """

    template = "endian.dashboard.web.templates.systeminformationplugincontent"
    engine_name = "mako"

    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/systeminformationcontent.css')
    ]

    class JobsInformationPluginContent(FormField):
        """
        .. autoclass:: JobsInformationPluginContent
           Generates the the content of the JobsInformationPlugin.
        """

        template = "endian.dashboard.web.templates.jobsinformationplugincontent"
        engine_name = "mako"


class SystemInformationPlugin(CloseablePlugin):
    """
    A dashboard plugin which display informations about
    the firewall system.
    """

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/systeminformationplugin.js')
    ]

    pluginName = _("System Information Plugin")
    pluginDescription = _("Shows information about the firewall system.")

    def __init__(self, **params):
        CloseablePlugin.__init__(
            self,
            jsInitFunction="systeminformationpluginUpdate",
            initURL="/manage/status/status.dashboard.system",
            jsUpdateFunction="systeminformationpluginUpdate",
            updateURL="/manage/status/status.dashboard.system",
            header=getHostName(),
            content=SystemInformationPluginContent(**params),
            **params
        )
