"""
.. mod:: uplinkinformationplugin - Contains the dashboard plugin
 UpLinkInformationPlugin.

.. synopsis:: Contains the dashboard plugin UpLinkInformationPlugin.

.. automodule:: uplinkinformationplugin

Contains the dashboard plugin UpLinkInformationPlugin.

.. moduleauthor:: Alber Maximilian
"""
__all__ = [
    'UpLinkInformationPlugin'
]

from tw.forms import FormField
from tw.api import JSLink, CSSLink
from endian.core.i18n import _
from endian.emi.widgets import CloseablePlugin

class UpLinkInformationPluginContent(FormField):
    """
    .. autoclass:: UpLinkInformationPluginContent

    Generates the the content of the UpLinkInformationPlugin.
    """

    template = "endian.dashboard.web.templates.uplinkinformationplugincontent"
    engine_name = "mako"

    css = [
        CSSLink(link='/toscawidgets/resources/endian.dashboard.web/static/css/uplinkinformationcontent.css')
    ]

    headers = [_('Name'),_('IP Address'),_('Status'),_('Uptime'),_('Active'),_('Managed'),""]

    params = ["headers"]

class UpLinkInformationPlugin(CloseablePlugin):
    """
    .. autoclass:: UpLinkInformationPlugin

    A dashboard plugin which display informations about
    the uplinks of the firewall system.
    """

    javascript = CloseablePlugin.javascript + [
        JSLink(link='/toscawidgets/resources/endian.dashboard.web/static/js/uplinkinformationplugin.js')
    ]

    pluginName = _("Uplink Information Plugin")
    pluginDescription = _("Shows information about the uplinks of the firewall.")

    def __init__(self, **params):
        """
        .. autofunction:: __init__

        Initilaze the object.
        """
        CloseablePlugin.__init__(
            self,
            jsInitFunction="uplinkinformationpluginInit",
            initURL="/manage/status/status.uplinksdaemon.data?timeout=2",
            jsUpdateFunction="uplinkinformationpluginUpdate",
            updateURL="/manage/status/status.uplinksdaemon.data?timeout=2",
            header=_("Uplinks"),
            content=UpLinkInformationPluginContent(**params),
            **params
        )
