import os

__author__ = "Peter Warasin <peter@endian.com>"
__date__ = "2008-01-01"


"""
Holds configuration constants. Default values are set, can be overriden
defining the corresponding ENDIAN_DS_* environment variable or by overriding
it using setConstant()

Current list:
  - PREFIX: prefix each directory with this value
  - USER_D: where the user settings files are stored (/var/efw)
  - PERSISTENT_D: where the persistent (packaged) config files
                  are stored (/usr/lib/efw)
  - STATE_D: where the live data (created by the system) is stored
             (/var/lib/efw)
  - PROVISIONING_D: where the provisioning settings files are stored (/var/emc)

"""

__all__ = [
    'IGNORESUFFICES',
    'DEVMODE',
    'constants',
    'setConstant',
]

# Obsolete
DEVMODE = False

IGNORESUFFICES = [
    'old',
    'rpmsave',
    'rpmnew',
    'orig',
    'rej',
    ]

constants={}

constants['PREFIX'] = os.environ.get('ENDIAN_DS_PREFIX', '/')
if 'ENDIAN_DS_USER_D' not in os.environ:
    constants['USER_D']=os.path.join(constants['PREFIX'], 'var/efw')
else:
    constants['USER_D']=os.environ['ENDIAN_DS_USER_D']

if 'ENDIAN_DS_PERSISTENT_D' not in os.environ:
    constants['PERSISTENT_D']=os.path.join(constants['PREFIX'], 'usr/lib/efw')
else:
    constants['PERSISTENT_D']=os.environ['ENDIAN_DS_PERSISTENT_D']

if 'ENDIAN_DS_STATE_D' not in os.environ:
    constants['STATE_D']=os.path.join(constants['PREFIX'], 'var/lib/efw')
else:
    constants['STATE_D']=os.environ['ENDIAN_DS_STATE_D']

if 'ENDIAN_DS_PROVISIONING_D' not in os.environ:
    constants['PROVISIONING_D']=os.path.join(constants['PREFIX'], 'var/emc')
else:
    constants['PROVISIONING_D']=os.environ['ENDIAN_DS_PROVISIONING_D']


def setConstant(key, value):
    """
    Sets the constant 'key' to 'value'

    :param key: the key to be set
    :type  key: string
    :param value: the value
    :type  value: string
    """
    constants[key]=value

