__author__ = "Peter Warasin <peter@endian.com>"
__date__ = "2008-01-07"

"""
Simple filesystem driver for Endian datasource
"""

import os
from endian.data.exceptions import InvalidGetMethod
from endian.data.container.fileconfig import getFileContent, PlainFile

__all__ = [ 'PlainFile' ]

def get_plain_file(key, obj, res, configfiles=None, raise_exceptions=False):
    " Return a plain file"
    filename = os.path.join(obj.abspath, key)
    content = getFileContent(filename, res)
    return content

def get_directory(key, obj, res, configfiles=None, raise_exceptions=False):
    " If the path is a directory, return a DataSource instance for the given directory "
    if os.path.isdir(os.path.join(obj.abspath, key)):
        try:
            from endian.data import DataSource
            return DataSource(os.path.join(obj.path, key), root=obj.root)
        except:
            pass
    raise InvalidGetMethod()

def init():
    from endian.data.methods import register_lookup
    register_lookup(10, 'get', get_directory, 'leaf', '*')
    register_lookup(1500, 'get', get_plain_file, 'leaf', '*', 'plain')

