__author__ = "Peter Warasin <peter@endian.com>"
__date__ = "2008-01-02"

"""
extended Json implementation which saves dictionaries
as ConfigDict (case insensitive and access to dictionary
fields as field variables) and allows to read and dump
JsonDumpable classes
"""

from endian.datatypes.config_dict import ConfigDict
import simplejson

__all__ = ['ConfigJSONEncoder',
           'JSONConfigDict',
           ]

class ConfigJSONEncoder(simplejson.JSONEncoder):
     def default(self, obj):
         if isinstance(obj, ConfigDict):
             return dict(obj.data)
         return simplejson.JSONEncoder.default(self, obj)

def JSONConfigDict(pairs):
    if type(pairs) == type({}):
        return ConfigDict(pairs)
