from endian.core.i18n import _
from endian.validators.network import IPAddress, MACAddress
from endian.emi import entities
from endian.emi.widgets.masked import MaskedField

__all__ = [
    'FixedLease'
]

class MACAddressField(MaskedField):
    mask = '**:**:**:**:**:**'

class FixedLease(entities.Entity):
    entities.using_options(namespace='dhcp.fixleases', type='csv', register_datasource=True)

    ID = entities.Field(entities.Unicode,
        primary_key=True,
        hidden=True,
        autoincrement=True)
    mac_address = entities.Field(entities.Unicode,
        label_text=_("MAC address"),
        nullable=False,
        widget=MACAddressField,
        validator=MACAddress)
    address = entities.Field(entities.Unicode,
        label_text=_("IP address"),
        nullable=False,
        validator=IPAddress)
    enabled = entities.Field(entities.Boolean,
        label_text=_("Enabled"),
        default=True)
    next_address = entities.Field(entities.Unicode,
        label_text=_("Next address"),
        validator=IPAddress)
    filename = entities.Field(entities.Unicode,
        label_text=_("Filename"))
    root_path = entities.Field(entities.Unicode,
        label_text=_("Root path"))
    remark = entities.Field(entities.Unicode,
        label_text=_("Remark"))

