from endian.core.i18n import _
from endian.emi import entities
from endian.validators.network import IPAddress

__all__ = [
    'DHCPServerSettings',
]

class DHCPServerSettings(entities.Entity):
    entities.using_options(namespace='dhcp.settings', type='settings', register_datasource=True)

    FIX_ENABLED = entities.Field(entities.Boolean,
        hidden=True,
        default=True)

    CUSTOM_GLOBAL = entities.Field(entities.UnicodeText,
        label_text=_("Custom configuration lines"))

    ENABLE_GREEN = entities.Field(entities.Boolean,
        label_text=_("Enable DHCP server on GREEN interface"),
        default=False)

    START_ADDR_GREEN = entities.Field(entities.Unicode,
        label_text=_("Start address"),
        validator=IPAddress)

    END_ADDR_GREEN = entities.Field(entities.Unicode,
        label_text=_("End address"),
        validator=IPAddress)

    ONLY_FIXEDLEASE_GREEN = entities.Field(entities.Boolean,
        label_text=_("Allow only fixed leases"),
        expands=True,
        default=False)

    DEFAULT_LEASE_TIME_GREEN = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Default lease time (min)"),
        nullable=False,
        default=60)

    MAX_LEASE_TIME_GREEN = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Max lease time (min)"),
        nullable=False,
        default=60)

    DOMAIN_NAME_GREEN = entities.Field(entities.Unicode,
        label_text=_("Domain name suffix"),
        nullable=True)

    GATEWAY_GREEN = entities.Field(entities.Unicode,
        label_text=_("Default gateway"),
        validator=IPAddress)

    DNS1_GREEN = entities.Field(entities.Unicode,
        label_text=_("Primary DNS"),
        nullable=True,
        validator=IPAddress)

    DNS2_GREEN = entities.Field(entities.Unicode,
        label_text=_("Secondary DNS"),
        nullable=True,
        validator=IPAddress)

    NTP1_GREEN = entities.Field(entities.Unicode,
        label_text=_("Primary NTP server"),
        nullable=True,
        validator=IPAddress)

    NTP2_GREEN = entities.Field(entities.Unicode,
        label_text=_("Secondary NTP server"),
        nullable=True,
        validator=IPAddress)

    WIN1_GREEN = entities.Field(entities.Unicode,
        label_text=_("Primary WINS server address"),
        nullable=True,
        validator=IPAddress)

    WIN2_GREEN = entities.Field(entities.Unicode,
        label_text=_("Secondary WINS server address"),
        nullable=True,
        validator=IPAddress)

    ENABLE_BLUE = entities.Field(entities.Boolean,
        label_text=_("Enable DHCP server on BLUE interface"),
        default=False)

    START_ADDR_BLUE = entities.Field(entities.Unicode,
        label_text=_("Start address"),
        # nullable=False,
        validator=IPAddress)

    END_ADDR_BLUE = entities.Field(entities.Unicode,
        label_text=_("End address"),
        # nullable=False,
        validator=IPAddress)

    ONLY_FIXEDLEASE_BLUE = entities.Field(entities.Boolean,
        label_text=_("Allow only fixed leases"),
        expands=True,
        default=False)

    DEFAULT_LEASE_TIME_BLUE = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Default lease time (min)"),
        nullable=False,
        default=60)

    MAX_LEASE_TIME_BLUE = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Max lease time (min)"),
        nullable=False,
        default=60)

    DOMAIN_NAME_BLUE = entities.Field(entities.Unicode,
        label_text=_("Domain name suffix"),
        nullable=True)

    GATEWAY_BLUE = entities.Field(entities.Unicode,
        label_text=_("Default gateway"),
        # nullable=False,
        validator=IPAddress)

    DNS1_BLUE = entities.Field(entities.Unicode,
        label_text=_("Primary DNS"),
        nullable=True,
        validator=IPAddress)

    DNS2_BLUE = entities.Field(entities.Unicode,
        label_text=_("Secondary DNS"),
        nullable=True,
        validator=IPAddress)

    NTP1_BLUE = entities.Field(entities.Unicode,
        label_text=_("Primary NTP server"),
        nullable=True,
        validator=IPAddress)

    NTP2_BLUE = entities.Field(entities.Unicode,
        label_text=_("Secondary NTP server"),
        nullable=True,
        validator=IPAddress)

    WIN1_BLUE = entities.Field(entities.Unicode,
        label_text=_("Primary WINS server address"),
        nullable=True,
        validator=IPAddress)

    WIN2_BLUE = entities.Field(entities.Unicode,
        label_text=_("Secondary WINS server address"),
        nullable=True,
        validator=IPAddress)

    ENABLE_ORANGE = entities.Field(entities.Boolean,
        label_text=_("Enable DHCP server on ORANGE interface"),
        default=False)

    START_ADDR_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Start address"),
        # nullable=False,
        validator=IPAddress)

    END_ADDR_ORANGE = entities.Field(entities.Unicode,
        label_text=_("End address"),
        # nullable=False,
        validator=IPAddress)

    ONLY_FIXEDLEASE_ORANGE = entities.Field(entities.Boolean,
        label_text=_("Allow only fixed leases"),
        expands=True,
        default=False)

    DEFAULT_LEASE_TIME_ORANGE = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Default lease time (min)"),
        nullable=False,
        default=60)

    MAX_LEASE_TIME_ORANGE = entities.Field(entities.Integer(not_empty=True, min=0),
        label_text=_("Max lease time (min)"),
        nullable=False,
        default=60)

    DOMAIN_NAME_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Domain name suffix"),
        nullable=True)

    GATEWAY_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Default gateway"),
        # nullable=False,
        validator=IPAddress)

    DNS1_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Primary DNS"),
        nullable=True,
        validator=IPAddress)

    DNS2_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Secondary DNS"),
        nullable=True,
        validator=IPAddress)

    NTP1_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Primary NTP server"),
        nullable=True,
        validator=IPAddress)

    NTP2_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Secondary NTP server"),
        nullable=True,
        validator=IPAddress)

    WIN1_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Primary WINS server address"),
        nullable=True,
        validator=IPAddress)

    WIN2_ORANGE = entities.Field(entities.Unicode,
        label_text=_("Secondary WINS server address"),
        nullable=True,
        validator=IPAddress)

