from endian.data.ds import DataSource
import os

def main():
    ds = DataSource('dnsmasq').settings
    schedule = ds.get('DNSMASQ_UPDATE_SCHEDULE')

    if schedule in ['hourly','daily','weekly','monthly']:
        if os.path.exists('/etc/cron.%s/blackholedns'%schedule):
            os.rename('/etc/cron.%s/blackholedns'%schedule,
                      '/etc/anacron.%s/blackholedns'%schedule)


if __name__ == '__main__':
    main()
