from endian.data.ds import DataSource
import os

def main():
    ds = DataSource('dnsmasq').settings

    changed = False
    if ds.get('DNSMASQ_UPDATE_SCHEDULE') == 'hourly':
        changed = True
        ds['DNSMASQ_UPDATE_SCHEDULE'] = 'daily'
        if os.path.exists('/etc/anacron.hourly/blackholedns'):
            os.rename('/etc/anacron.hourly/blackholedns',
                      '/etc/anacron.daily/blackholedns')
        elif os.path.exists('/etc/cron.hourly/blackholedns'):
            os.rename('/etc/cron.hourly/blackholedns',
                      '/etc/anacron.daily/blackholedns')

    if ds.get('DNSMASQ_BLACKHOLE_REDIRECT') == '139.146.167.25':
        changed = True
        ds['DNSMASQ_BLACKHOLE_REDIRECT'] = '75.125.225.163'

    if 'www.malware' in ds.get('DNSMASQ_BLACKHOLE_URL'):
        changed = True
        domains = ds.get('DNSMASQ_BLACKHOLE_URL')
        ds['DNSMASQ_BLACKHOLE_URL'] = domains.replace('www.malware','mirror2.malware')

    if changed:
        ds.write()

if __name__ == '__main__':
    main()
