import os
from endian.job.commons import get_bypasses
from endian.data.ds import DataSource

TRANSPARENT_SOURCE_BYPASS = "/var/efw/dnsmasq/source_bypass"
TRANSPARENT_DESTINATION_BYPASS  = "/var/efw/dnsmasq/destination_bypass"

def main():

    bypasses = get_bypasses(TRANSPARENT_SOURCE_BYPASS, TRANSPARENT_DESTINATION_BYPASS)

    ds = DataSource("dnsmasq").settings

    sources = []
    destinations = []

    for bypass in bypasses:
        if bypass.get("SRC"):
            sources.append(bypass["SRC"])
        elif bypass.get("MAC"):
            sources.append(bypass["MAC"])
        elif bypass.get("DST"):
            destinations.append(bypass["DST"])

    ds["DNSMASQ_TRANSPARENT_SOURCE_BYPASS"] = "&".join(sources)
    ds["DNSMASQ_TRANSPARENT_DESTINATION_BYPASS"] = "&".join(destinations)

    ds.write()

    if os.path.exists(TRANSPARENT_SOURCE_BYPASS):
        os.unlink(TRANSPARENT_SOURCE_BYPASS)

    if os.path.exists(TRANSPARENT_DESTINATION_BYPASS):
        os.unlink(TRANSPARENT_DESTINATION_BYPASS)



if __name__ == '__main__':

    main()
