from endian.core.i18n import _
from endian.emi import entities
from endian.validators.network import IPAddress, HostnameRegex, DomainNameRegex

__all__ = [
    "Host",
    "SystemHost",
    "HostnameOrWildcard"
]

class HostnameOrWildcard(HostnameRegex):
    """
        Validator to check whether a string is a valid host name or whildcard
    """

    def _to_python(self, value, state):
        if value == '*':
            return value
        else:
            return HostnameRegex._to_python(self, value, state)


class Host(entities.Entity):

    entities.using_options(namespace="dnsmasq.hosts", type="csv", register_datasource=True)

    ID = entities.Field(entities.Integer,
        primary_key=True,
        auto_increment=True,
        hidden=True,
    )

    enabled = entities.Field(entities.Boolean,
        expands=True,
        label_text=_("Enabled"),
        default=True
    )

    ip = entities.Field(entities.Unicode,
        expands=True,
        label_text=_("IP address"),
        default="",
        nullable=False,
        validator=IPAddress
    )

    hostname = entities.Field(entities.Unicode,
        label_text=_("Hostname"),
        default="",
        nullable=False,
        validator=HostnameOrWildcard
    )

    domainname = entities.Field(entities.Unicode,
        label_text=_("Domain name"),
        default="",
        nullable=True,
        validator=DomainNameRegex(require_fqdn=False)
    )

    remark = entities.Field(entities.Unicode,
        label_text=_("Remark"),
        default=""
    )


class SystemHost(entities.Entity):

    entities.using_options(namespace="dnsmasq.system_hosts", type="csv", register_datasource=True)

    ID = entities.Field(entities.Integer,
        primary_key=True,
        auto_increment=True,
        hidden=True,
    )

    enabled = entities.Field(entities.Boolean,
        default=True
    )

    ip = entities.Field(entities.Unicode,
        default=""
    )

    hostname = entities.Field(entities.Unicode,
        default="",
    )

    domainname = entities.Field(entities.Unicode,
        default=""
    )

    remark = entities.Field(entities.Unicode,
        default=""
    )
