from endian.core.i18n import _
from endian.emi import entities
from endian.validators.network import IPAddress, DomainNameRegex

__all__ = [
    "LocalDomain",
    "SystemLocalDomain"
]


class LocalDomain(entities.Entity):
    entities.using_options(namespace="dnsmasq.local_nameserver", type="csv", register_datasource=True)

    ID = entities.Field(
        entities.Integer,
        primary_key=True,
        auto_increment=True,
        hidden=True
    )

    enabled = entities.Field(
        entities.Boolean,
        label_text=_("Enabled"),
        default=True,
        expands=True
    )

    domain = entities.Field(
        entities.Unicode,
        label_text=_("Domain name"),
        default="",
        validator=DomainNameRegex(require_fqdn=False),
        nullable=False
    )

    ip = entities.Field(
        entities.Unicode,
        label_text=_("IP address"),
        default="",
        validator=IPAddress,
        nullable=False
    )

    remark = entities.Field(
        entities.Unicode,
        label_text=_("Remark"),
        default="",
        expands=True
    )

class SystemLocalDomain(entities.Entity):
    entities.using_options(namespace="dnsmasq.system_local_nameserver", type="csv", register_datasource=True)

    ID = entities.Field(
        entities.Integer,
        primary_key=True,
        auto_increment=True,
        hidden=True
    )

    enabled = entities.Field(
        entities.Boolean,
        default=True
    )

    domain = entities.Field(
        entities.Unicode,
        default=""
    )

    ip = entities.Field(
        entities.Unicode,
        default=""
    )

    remark = entities.Field(
        entities.Unicode,
        label_text=_("Remark"),
        default=""
    )
