from endian.core.i18n import _
from endian.emi import controllers
from endian.dnsmasq.web.widgets.antispyware import AntispywareEditor
from endian.dnsmasq.schema.settings import Settings
from endian.job.engine_control import send_cmd_to_engine

__all__ = [
    "AntispywareSwitchController"
]

class AntispywareEditorController(controllers.SettingsController):

    settingsWidget = AntispywareEditor

    def _apply(self, **args):
        self.storage.changed = False
        send_cmd_to_engine("restart sync dnsblackhole")


class AntispywareSwitchController(controllers.SwitchController):

    switch_title = _("Enable")

    description = _("To enable the antispyware click on the switch above.")

    switch_divider = True
    show_divider = True

    entity = Settings

    key = "DNSMASQ_ANTISPYWARE"

    def action(self):
        send_cmd_to_engine("restart sync dnsblackhole")

    settings = AntispywareEditorController(name="settings")

    controllers = [settings]
