from endian.emi import controllers
from endian.dnsmasq.web.widgets.localdomains import LocalDomainEditor, LocalDomainGrid, SystemLocalDomainGrid
from endian.job.engine_control import send_cmd_to_engine

__all__ = [
    "LocalDomainController"
]

class LocalDomainGridController(controllers.GridController):

    editorWidget = LocalDomainEditor

    gridWidget = LocalDomainGrid

    def _apply(self, **args):
        self.storage.changed = False
        send_cmd_to_engine("restart sync dnsmasq")

    def onDelete(self, id, data):
        self.storage.changed = True
        return super(LocalDomainGridController, self).onDelete(id, data)


class SystemLocalDomainGridController(controllers.GridController):

    gridOnly = True

    editorWidget = None

    gridWidget = SystemLocalDomainGrid


class LocalDomainController(controllers.MultiController):

    localdomains = LocalDomainGridController(name="localdomains")

    systemlocaldomains = SystemLocalDomainGridController(name="systemlocaldomains")

    controllers = [localdomains, systemlocaldomains]
