from endian.core.i18n import _
from endian.emi import widgets, fieldset
from endian.dnsmasq.schema.settings import Settings

__all__ = [
    "DnsProxyEditor"
]

class DnsProxyEditor(widgets.SettingsEditor):

    title = ""

    entity = Settings

    children = fieldset.FieldsGroup(
        fields=[
            fieldset.FieldsGroup(
                fields=Settings.fields(
                    fields=[
                        "DNSMASQ_TRANSPARENT_GREEN",
                        "DNSMASQ_TRANSPARENT_BLUE",
                        "DNSMASQ_TRANSPARENT_ORANGE",
                    ]
                )
            ),
            fieldset.FieldsGroup(
                info_text=_("Bypass the transparent proxy"),
                fields=Settings.fields(
                    fields=[
                        "DNSMASQ_TRANSPARENT_SOURCE_BYPASS",
                        "DNSMASQ_TRANSPARENT_DESTINATION_BYPASS",
                    ]
                )
            )
        ]
    )
