from endian.core.i18n import _
from endian.emi import widgets, fieldset
from endian.dnsmasq.schema.hosts import Host, SystemHost

__all__ = [
    "HostEditor",
    "HostGrid",
    "SystemHostGrid"
]

class HostEditor(widgets.GridEditor):

    title = _("Add new host")

    edit_title = _("Change host")

    entity = Host

    children = fieldset.FieldsGroup(
        fields=Host.fields(fields=[
            "ip",
            "hostname",
            "domainname",
            "remark",
            "enabled",
            "ID"])
        )

class HostGrid(widgets.KendoGrid):
    title = ""

    sortable = True

    order_by = "ip"

    order_sort = "asc"

    new_label = _("Add new host")

    entity = Host

    columns = [
        widgets.Column(name="ip", title=_("IP address"), empty_value=""),
        widgets.Column(name="hostname", title=_("Hostname"), empty_value=""),
        widgets.Column(name="domainname", title=_("Domain name"), empty_value=""),
        widgets.ActionsColumn(["enable", "edit", "deleteConfirm"])
    ]

class SystemHostGrid(widgets.KendoGrid):

    title = _("Hosts for system services")

    sortable = True

    order_by = "ip"

    order_sort = "asc"

    new_label = _("Create new host")

    entity = SystemHost

    columns = [
        widgets.Column(name="ip", title=_("IP address"), empty_value=""),
        widgets.Column(name="hostname", title=_("Hostname"), empty_value=""),
        widgets.Column(name="domainname", title=_("Domain name"), empty_value="")
    ]

