from endian.core.i18n import _
from endian.emi import widgets, fieldset
from endian.dnsmasq.schema.localdomains import LocalDomain, SystemLocalDomain

__all__ = [
    "LocalDomainEditor",
    "LocalDomainGrid",
    "SystemLocalDomainGrid"
]

class LocalDomainEditor(widgets.GridEditor):

    title = _("Add new custom nameserver for a domain")

    edit_title = _("Change custom nameserver")

    entity = LocalDomain

    children = (
        fieldset.FieldsGroup(
            fields=LocalDomain.fields(
                fields=[
                    "domain",
                    "ip",
                    "remark",
                    "enabled",
                    "ID"
                ]
            )
        )
    )

class LocalDomainGrid(widgets.KendoGrid):
    title=""

    sortable = True

    order_by = "domain"

    order_sort = "asc"

    new_label= _("Add new custom name server for a domain")

    entity = LocalDomain

    columns = [
        widgets.Column(
            name="domain",
            title=_("Domain name"),
            empty_value=""
        ),
        widgets.Column(
            name="ip",
            title=_("IP Address"),
            empty_value=""
        ),
        widgets.Column(
            name="remark",
            title=_("Remark"),
            empty_value=""
        ),
        widgets.ActionsColumn([
                "enable",
                "edit",
                "deleteConfirm"
            ]
        )
    ]

class SystemLocalDomainGrid(widgets.KendoGrid):

    title=_("Local domains for system services")

    sortable = True

    order_by = "domain"

    order_sort = "asc"

    new_label=_("Create new local domain")

    entity = SystemLocalDomain

    columns = [
        widgets.Column(
            name="domain",
            title=_("Domain name"),
            empty_value=""
        ),
        widgets.Column(
            name="ip",
            title=_("IP Address"),
            empty_value=""
        ),
        widgets.Column(
            name="remark",
            title=_("Remark"),
            empty_value=""
        )
    ]
