import os
import mimetypes
import pkg_resources
from base import Controller, expose, redirect
from endian.emi.framework import HTTPError, response

class ResourcesController(Controller):
    """
        This controller serves static resources
    """
    
    @expose()
    def resources(self, *args, **argv):
        try:
            if not args:
                raise HTTPError(404, u"Not found")
            package = args[0]
            relative_path = "/".join(args[1:])
            if '..' in relative_path:
                raise HTTPError(404, u"Not found")
            # Guess the type of a file based on its filename or URL         
            content_type, charset = mimetypes.guess_type(os.path.basename(relative_path))
            if not content_type:
                content_type = "text/plain"
            response.headers['Content-Type'] = content_type
            if charset:
                response.headers['charset'] = charset
            # Retrieve the file
            return pkg_resources.resource_string(package, relative_path)
        except IOError:
            raise HTTPError(404, u"Not found")
        except ImportError:
            raise HTTPError(404, u"Not found")

__all__ = [
    'ResourcesController'
]
