from endian.core.logger import debug
from cherrypy import request, response
from cPickle import loads, dumps


def setCookie(name, value, path=None):
    cookies = response.simple_cookie
    cookies[name] = dumps(value)

    if not path:
        path = request.path_info
    cookies[name]['path'] = path
    debug("COOKIE: Setting %s=%s [%s]", name, value, path)
    return True


def getCookie(name, default=None):
    cookies = request.simple_cookie

    try:
        value = loads(cookies[name].value)
    except:
        value = default

    debug("COOKIE: Getting %s=%s", name, value)
    return value
