def __get_widget_validation_fields_dict(entity):
    for field_name, field in entity._db_fields.items():
        if "validator" in field.info:
            field.info["widget_validator"] = field.info["validator"]
            del field.info["validator"]
    ret = entity.fieldsDict()
    for field_name, field in entity._db_fields.items():
        if "widget_validator" in field.info:
            field.info["validator"] = field.info["widget_validator"]
            del field.info["widget_validator"]
    return ret
