from UserDict import DictMixin
from endian.core.logger import warn

DEFAULT_MENU_ORDER = 10000

__all__ = ['MenuLevel', 'DEFAULT_MENU_ORDER']

class MenuLevel(DictMixin):
    """
        Ordered dictionary of menu items (of the same menu)
        The elements are order by the value of the 'menu_order' attribute and than alphabetically
    """

    def __init__(self, data=None):
        """
        Create a e menu level
        'data' is an unsorted dictionary representing the menu level elements
        """
        self._data = {}
        self._keys = [x[0] for x in sorted(data.iteritems(), key=self.menu_sort_function)]
        for (k,v) in data.iteritems():
            values = {}
            if not v:
                warn("Missing value in menu for key: %s" % k)
            else:
                for (kv,vv) in v.iteritems():
                    values[kv] = (kv == 'subMenu') and MenuLevel(vv) or vv
            self._data[k] = values

    @staticmethod
    def menu_sort_function(value):
        (k, v) = value
        try:
            return (v['menu_order'], k)
        except:
            return (DEFAULT_MENU_ORDER, k)

    def keys(self):
        return list(self._keys)

    def __getitem__(self, key):
        return self._data[key]

    def __repr__(self):
        result = []
        for key in self._keys:
            result.append('%s: %s' % (repr(key), repr(self._data[key])))
        return ''.join(['{', ', '.join(result), '}'])
