from endian.emi.widgets.container import Container
from tw.forms import TableForm
from tw.api import CSSLink, Widget

class ContainerForm(Container, TableForm):
    css_classes=["containerform"]
    css = [CSSLink(link='/toscawidgets/resources/endian.emi/static/css/container.css')]
    template = "endian.emi.templates.containerform"
    engine_name = "mako"

    columns = 2
    expands = True
    strip_name = True
    show_labels = True
    submit_text = None
    params = ["expands", "show_labels", "columns"]
    
    def __init__(self, id=None, parent=None, children=[], **kw):
        self.submit_text = None
        super(ContainerForm, self).__init__(id=id, parent=parent, children=children, **kw)

__all__ = ['ContainerForm']
