from datetime import datetime
from tw.forms import FormField
from tw.forms.validators import DateTimeConverter
from static import jquery_ui_datepicker, jquery_ui_datepicker_css
import endian.core.i18n

class DatePicker(FormField):
    """
    Uses jQuery UI datepicker to allow picking of calendar dates.
    The date_format is in dd/mm/yyyy unless otherwise specified
    """
    css = [jquery_ui_datepicker_css]
    javascript = [jquery_ui_datepicker]
    template = "endian.emi.templates.datepicker"
    engine_name = "mako"
    
    params = ["not_empty", "readonly", "empty_value", "date_format", "js_date_format"]
    not_empty = True
    readonly = False
    empty_value = None
    date_format = "%d/%m/%Y"
    validator = None
    _default = None

    @property
    def js_date_format(self):
        """
        Convert python format https://docs.python.org/2/library/datetime.html
        to jQuery UI Datepicker format http://api.jqueryui.com/datepicker/#option-dateFormat
        """
        return self.date_format.replace("%d", "dd").replace("%m", "mm").replace("%Y", "yy").replace("%y", "y")

    def __init__(self, *args, **kw):
        super(DatePicker, self).__init__(*args, **kw)
        if self.default is None and self.not_empty:
            self.default = lambda: datetime.now()
        if not self.readonly:
            self.validator = self.validator or DateTimeConverter(format=self.date_format, not_empty=self.not_empty)

    def update_params(self, d):
        super(DatePicker, self).update_params(d)
        try:
            d.strdate = d.value.strftime(self.date_format)
        except AttributeError:
            if not d.value and self.empty_value:
                d.value = self.empty_value
            d.strdate = d.value

__all__ = ['DatePicker']
