from tw.forms import FieldSet, FormField

import endian.core.i18n
from endian.emi.widgets.static import *

class GalleryImage(FormField):
    template = "endian.emi.templates.galleryimage"
    engine_name = "mako"
    
    css = []
    
    title = ""
    description = ""
    picture = ""
    
    params = ["title", "description", "picture"]
    
    def __init__(self, title="", description="", picture=None, **params):
        FormField.__init__(self, **params)
        self.title = title
        self.description = description
        self.picture = picture.link

class Gallery(FieldSet):
    template = "endian.emi.templates.gallery"
    engine_name = "mako"
    
    css = [
        CSSLink(link='/toscawidgets/resources/endian.emi/static/css/gallery.css', css=[style_css]),
        jquery_tools
    ]

__all__ = [
    'GalleryImage',
    'Gallery'
]