from tw.forms import HiddenField
from tw.api import CSSLink, Widget

from settingseditor import SettingsEditor

class GridEditor(SettingsEditor):
    engine_name = "mako"
    
    css_classes = [
        "grideditor"
    ]
    
    title = _("Add")
    edit_title = _("Edit")
    
    form = True
    
    params = [
        "edit_title",
        "form"
    ]
    
    def __init__(self, **kw):
        if not self.template:
            self.template = "endian.emi.templates.grideditor"
        self.css.append(CSSLink(link='/toscawidgets/resources/endian.emi/static/css/grideditor.css'))
        if not self.entity:
            HiddenField("ID", self)
        super(GridEditor, self).__init__(**kw)

class RuleEditor(GridEditor):
    '''
    @deprecated: RulesEditor has been deprecated: use GridEditor instead.
    '''
    
    def __init__(self, *args, **kargs):
        import warnings
        warnings.warn("RulesEditor has been deprecated: use GridEditor instead.", 
                      DeprecationWarning, 2)
        GridEditor.__init__(self, *args, **kargs)

__all__ = [
    'GridEditor',
    'RuleEditor'
]