from tw.forms import InputField
from tw.api import\
    Link,\
    CSSLink,\
    JSLink

import endian.core.i18n
from endian.emi.widgets.static import\
    jquery_js,\
    logger_js

__all__ = ["InputLock"]

class InputLock(InputField):
    template = "endian.emi.templates.input_lock"
    engine_name = "mako"
    
    css = [
        CSSLink(link='/toscawidgets/resources/endian.emi/static/css/input_lock.css')
    ]
    
    javascript = [JSLink(link='/toscawidgets/resources/endian.emi/static/js/input_lock.js',
                         javascript=[jquery_js,
                                     logger_js])
                  ]
    type = "hidden"
    
    #config
    target_id = None#a jquery selector!
    message = _("You have made custom settings. You wish to overwrite those settings?")
    
    params = ["message",
              "target_id"]
    
    def __init__(self,
                 target_id=None,
                 message=None,
                 **params):
        if message is not None:
            self.message = message
        if target_id is not None:
            self.target_id = target_id
        if not self.target_id and "id" in params:
            self.target_id = "[name='%s']" % params["id"][:-5]# without "_LOCK"
        InputField.__init__(self, **params)
