from tw.api import Widget, Link, CSSLink
from static import jquery_js, jquery_emi_apply

class Notification(Widget):
    template = ""
    engine_name = "mako"
    css = [
        CSSLink(link='/toscawidgets/resources/endian.emi/static/css/notification.css')
    ]
    javascript = [jquery_js]
    
    params = []

class ImportantNotification(Notification):
    template = "endian.emi.templates.notification_important"
    engine_name = "mako"
    css_classes = ["important-fancy"]
    javascript = [
        jquery_emi_apply
    ]
    
    controllername = ""
    action = ""
    button = _("Apply")
    action_value = "apply"
    
    params = ["controllername", "action", "button", "action_value"]
    
    def __init__(self, *args, **kargs):
        self.controllername = kargs.get('controllername', "")
        Notification.__init__(self, *args, **kargs)

class InfoNotification(Notification):
    template = "endian.emi.templates.notification_info"
    engine_name = "mako"
    css_classes = ["notification-fancy"]

class ErrorNotification(Notification):
    template = "endian.emi.templates.notification_error"
    engine_name = "mako"
    css_classes = ["error-fancy"]

__all__ = [
    'ImportantNotification',
    'InfoNotification',
    'ErrorNotification'
]