from tw.api import JSLink
from tw.forms import InputField
from endian.emi.widgets.static import base32_js, qrcode_js, jquery_js, jquery_ui_dialog
from endian.core.i18n import _

__all__ = ["OTPSecretInputField"]

class OTPSecretInputField(InputField):

    template = "endian.emi.templates.otp"
    engine_name = "mako"
    javascript = [JSLink(link='/toscawidgets/resources/endian.emi/static/js/otp.js',
                  javascript=[jquery_js, base32_js, qrcode_js, jquery_ui_dialog]) ]
    reset_label = _("Generate new secret")
    show_qr_code_label = _("Show QR Code")
    name_field = "name"
    issuer_field = "domain"
    params = ["reset_label", "show_qr_code_label", "name_field", "issuer_field"]

    def __init__(self, name_field=None, reset_label=None, show_qr_code_label=None, issuer_field=None, **params):
        if name_field is not None:
            self.name_field = name_field
        if reset_label is not None:
            self.reset_label = reset_label
        if show_qr_code_label is not None:
            self.show_qr_code_label = show_qr_code_label
        InputField.__init__(self, **params)

